function show(){var n=document.getElementById("snackbar");n.className="show";setTimeout(function(){n.className=n.className.replace("show","")},1300)}function copyToClipboard(n){var t=document.createElement("input");t.setAttribute("value",document.getElementById(n).value);document.body.appendChild(t);t.select();document.execCommand("copy");document.body.removeChild(t);show()}